local Players=game:GetService("Players") local LocalPlayer=Players.LocalPlayer local ADMIN_ID=10172860954 local keys={} keys["ENERGY-PERMANENTE-ADMIN"]={tipo="permanente",criada=0,deviceId=nil} local tempoExpira={diaria=86400,semanal=604800,permanente=math.huge} local function gerarKey(tipo) local charset="ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789" local key="" for i=1,6 do local r=math.random(1,#charset) key=key..charset:sub(r,r) end return "ENERGY-"..tipo:upper().."-"..key end local function gerarERegistrarKey(tipo,player) if not player or player.UserId~=ADMIN_ID then warn("[EnergyStudio] Tentativa não autorizada") return nil end local key=gerarKey(tipo) keys[key]={tipo=tipo,criada=os.time(),deviceId=nil} return key end local function validarKey(keyDigitada) keyDigitada=string.upper(keyDigitada:gsub("%s+","")) local data=keys[keyDigitada] if not data then return false,"Key inválida" end local agora=os.time() if (agora-data.criada)>tempoExpira[data.tipo] then return false,"Key expirada" end return true,"EnergyStudio liberado" end if LocalPlayer and LocalPlayer.UserId==ADMIN_ID then local novaKey=gerarERegistrarKey("permanente",LocalPlayer) if novaKey then print("[EnergyStudio] Nova key criada:",novaKey) end end
